		rec.enablebackup = current.variables.enableBackup;
		rec.subscriptiondiscription = current.variables.subscriptionDiscription;
		rec.businessinformation = current.variables.BusinessInformation;
		rec.securityzone = current.variables.securityzone;
		rec.active = true;
	
		
		workflow.info('Variables are updated now todating row');
		rec.update();
		}
	}
	catch(err){
		workflow.info('Error encounter while updation CMDB' + err); 
	}
	
	
}

insertRecord();
setClosed();

function setClosed() {
	var req = new GlideRecord('sc_request');
	req.addQuery('sys_id', current.request);
	req.query(); 
	while (req.next()) { 
	 req.request_state = 'closed_complete';
	 req.update();
	}
}


