insertRecord();
setClosed();

function setClosed() {
	var req = new GlideRecord('sc_request');
	req.addQuery('sys_id', current.request);
	req.query(); 
	while (req.next()) { 
	 req.request_state = 'closed_complete';
	 req.update();
	}
}


