	answer.push(current.variables.user.manager);

var activityno = 0;
if(current.variables.phase == "Deploy"){
	activityno = 10;
} else if(current.variables.phase == "Modify"){
	activityno = 11;
}else if(current.variables.phase == 'Decommission'){
	activityno = 12;
}else if(current.variables.phase == 'Action'){
	activityno = 27;
}
workflow.info("current.variables.phase : "+current.variables.phase);
workflow.info("data.get("+activityno+").statucode : "+data.get(activityno).status_code);

//workflow.info("es_status : "+data.get(activityno).output.executionSummary.status);
workflow.info("Responce Body : "+data.get(activityno).body);
workflow.info("Execution Id Body : "+ data.get(activityno).output.executionId);


if(data.get(activityno).status_code == 201){
	responseBodystr = data.get(activityno).body;
	responsebody = JSON.parse(responseBodystr);
	workflow.info("responsebody.executionId : "+responsebody.executionId);
	workflow.scratchpad.action_flow_executionId = responsebody.executionId;
	activity.result = 'success';
}
else{
	activity.result = 'failure';
}

